mapper.update:
update student


        violation_count = violation_count + 1,
        violation_score = violation_score + #{score}


where id = #{id}

mapper.select:
select s.*, c.id clazzId , c.name clazzName from student s  left join clazz c on c.id = s.clazz_id


        s.name like concat('%', #{name}, '%')


        and degree = #{degree}


        and clazz_id = #{clazzId}
